import './App.css';
import * as d3 from "d3"
import React, { useEffect, useRef } from 'react';

function Bubble() {
  const d3Container = useRef(null);

  useEffect(() => {
    if (d3Container.current) {
      const margin = {top: 10, right: 10, bottom: 10, left: 10},
            width = 640 - margin.left - margin.right,
            height = 640 - margin.top - margin.bottom;

      // Clear SVG to prevent duplication
      d3.select(d3Container.current).selectAll("*").remove();

      const svg = d3.select(d3Container.current)
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)
        .append("g")
        .attr("transform",
              "translate(" + margin.left + "," + margin.top + ")");

      // Generate the bubble chart here
      const root = d3.hierarchy({children: data}).sum(d => d.value);

      d3.pack()
        .size([width, height])
        .padding(3)(root);

      const node = svg.selectAll(".node")
        .data(root.children)
        .enter().append("g")
        .attr("class", "node")
        .attr("transform", d => `translate(${d.x},${d.y})`);

      node.append("circle")
        .attr("r", d => d.r)
        .style("fill", "#69b3a2");

      node.append("text")
        .text(d => d.data.id.substring(d.data.id.lastIndexOf(".") + 1))
        .attr("dy", ".2em")
        .style("text-anchor", "middle")
        .attr("font-size", d => `${d.r / 5}px`)
        .attr("fill", "white");
    }
  }, []); // Empty dependency array means this effect runs once on mount

  return <svg ref={d3Container} />;
}

function Upload() {

  return(
    <button id="upload">Upload</button>
  )
}

function App() {

  return (
    <div className="App">
      <div id="header-container">
        <h1 className="Header">dataset.csv</h1>
        <h2 className="Header" id="dashboard">Dashboard</h2>
        <Upload />
      </div>
      <Bubble />
      <div id="background-split">
      </div>
    </div>
  );
}

export default App;
